import React from "react";
import BurgerIngredientsStyles from "./burger-ingredients.module.css";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerIngredients(props) {
  let {type, setType} = React.useState('buns');
  const ingredients = props.data;

  function handleTabChange(evt) {
    setType(type = evt);
    handleScroll(evt);
  }

  function handleScroll(evt) {
    document.getElementById(`${evt}`).scrollIntoView({ behavior: "smooth", block: "start"});
  }

  return(
    <section className={`${BurgerIngredientsStyles.section} pl-5 pr-5`}>
      <div className={BurgerIngredientsStyles.content}>
        <h2 className={`${BurgerIngredientsStyles.heading} text text_type_main-large pt-10 pb-5 pl-5 pr-5`}>Соберите бургер</h2>

        <div className={`${BurgerIngredientsStyles.tabs} mb-10`}>
          <Tab value="buns" active={type === 'buns'} onClick={handleTabChange}>Булки</Tab>
          <Tab value="sauces" active={type === 'sauces'} onClick={handleTabChange}>Соусы</Tab>
          <Tab value="fillings" active={type === 'fillings'} onClick={handleTabChange}>Начинки</Tab>
        </div>

        <div className={`${BurgerIngredientsStyles.container} custom-scroll`}>
          <h3 className="text text_type_main-medium mb-6"></h3>
        </div>

      </div>
    </section>
  )
}

export default BurgerIngredients;
