import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import BurgerIngredientsStyles from "./burger-ingredients.module.css";
import React from "react";
import IngredientItem from "./ingredient-item/ingredent-item";
import IngredientGroup from "./ingredient-group/ingredient-group";
import IngredientDetails from "../ingredient-details/ingredient-details";
import Modal from "../modal/modal";
import PropTypes from "prop-types";
import {useModal} from "../../hooks/useModal";
import { useInView } from 'react-intersection-observer';

function BurgerIngredients({ ingredients }) {
  const [ingredent, setingredient] = React.useState(null);
  const { isModalOpen, openModal, closeModal } = useModal();

  const [bunsRef, bunsInView] = useInView({ threshold: 0 });
  const [saucesRef, saucesInView] = useInView({ threshold: 0 });
  const [fillingsRef, fillingsInView] = useInView({ threshold: 0 });

  const [type, setType] = React.useState("buns");

  const buns = ingredients.filter((item) => item.type === 'bun');
  const mains = ingredients.filter((item) => item.type === 'main');
  const sauces = ingredients.filter((item) => item.type === 'sauce');
  
  function handleScroll(evt) {
    document.getElementById(`${evt}`).scrollTo({ behavior: "smooth", block: "start"});
  }

  const handleIngredientClick = (item) => {
    setingredient(item);
    openModal();
  };

  function handleTabChange(evt) {
    setType(evt);
    handleScroll(evt);
  }

  React.useEffect(() => {
    if (bunsInView) {
      setType("buns");
    } else if (saucesInView) {
      setType("sauces");
    } else if (fillingsInView) {
      setType("fillings");
    }
  }, [bunsInView, saucesInView, fillingsInView]);

  return (
    <section className={BurgerIngredientsStyles.container}>
      <p className="text text_type_main-large pt-10 pb-5">Соберите бургер</p>
      <div className={`${BurgerIngredientsStyles.tab} pb-10`}>
      <Tab value="buns" active={type === 'buns'} onClick={handleTabChange}>Булки</Tab>
        <Tab value="sauces" active={type === 'sauces'} onClick={handleTabChange}>Соусы</Tab>
        <Tab value="fillings" active={type === 'fillings'} onClick={handleTabChange}>Начинки</Tab>
      </div>
      <div className={`${BurgerIngredientsStyles.containerScroll} custom-scroll`}>
        <IngredientGroup title={"Булки"}>
          {buns.map((item) => (
            <IngredientItem 
              image={item.image}
              name={item.name}
              price={item.price}
              counter={1}
              key={item._id}
              onClick={() => handleIngredientClick(item)}
            />
          ))}
        </IngredientGroup>
        <IngredientGroup title={"Соусы"}>
          {sauces.map((item) => (
            <IngredientItem 
              image={item.image}
              name={item.name}
              price={item.price}
              counter={1}
              key={item._id}
              onClick={() => handleIngredientClick(item)}
            />
          ))}
        </IngredientGroup>
        <IngredientGroup title={"Начинки"}>
          {mains.map((item) => (
            <IngredientItem 
              image={item.image}
              name={item.name}
              price={item.price}
              counter={1}
              key={item._id}
              onClick={() => handleIngredientClick(item)}
            />
          ))}
        </IngredientGroup>
      </div>
      {isModalOpen && (
        <Modal onClose={closeModal}>
          <IngredientDetails ingredient={ingredent} />
        </Modal>
      )}
    </section>
  );
}

BurgerIngredients.propTypes = {
  ingredients: PropTypes.array.isRequired,
}

export default BurgerIngredients;
