import BurgerComponents from './burger-components/burger-components';
import BurgerConstructorStyles from "./burger-constructor.module.css";
import {
  Button,
  CurrencyIcon,
} from "@ya.praktikum/react-developer-burger-ui-components"
import React from "react";
import Modal from "../modal/modal";
import OrderDetails from "../order-details/order-details";
import PropTypes from "prop-types";
import { useModal } from "../../hooks/useModal";

function BurgerConstructor({ ingredients }) {
  const { isModalOpen, openModal, closeModal } = useModal();
  return (
    <section className={`${BurgerConstructorStyles.container} pl-10 pt-25`}>
      <BurgerComponents data={ingredients} />
      <div className={`${BurgerConstructorStyles.order} pt-10 pr-4`}>
        <div className={`${BurgerConstructorStyles.price} pr-10`}>
          <p className="text text_type_digits-medium">600</p>
          <CurrencyIcon type="primary" />
        </div>
        <Button onClick={openModal} htmlType="button" type="primary" size="large">Оформить заказ</Button>
        {isModalOpen && (
          <Modal onClose={closeModal}>
            <OrderDetails />
          </Modal>
        )}
      </div>
    </section>
  )
}

BurgerConstructor.propTypes = {
  ingredients: PropTypes.array.isRequired,
};

export default BurgerConstructor;
