import React from "react";
import ReactDOM from "react-dom";
import ModalStyles from "./modal.module.css";
import PropTypes from "prop-types";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import ModalOverlay from "../modal-overlay/modal-overlay";

function Modal({ children, onClose, modalTitle }) {
  React.useEffect(() => {
    const handleEscapePress = (event) => {
      if (event.key === "Escape") {
        onClose();
      }
    };
    document.addEventListener("keydown", handleEscapePress);
    return () => {
      document.removeEventListener("keydown", handleEscapePress)
    };
  }, [onClose]);

  return ReactDom.createPortal (
    (
      <div className={ModalStyles.modal}>
        <ModalOverlay handleClose={onClose} />
        <div className={ModalStyles.content}>
          <header className={`${ModalStyles.header} ${modalTitle === "Детали ингредиента" ? "pt-10 pl-10 pr-10" : "pt-15 pl-10 pr-10"}`}>
            {modalTitle && <p className="text text_type_main-large">{modalTitle}</p>}
            <button className={ModalStyles.closeButton} onClick={onClose}>
              <CloseIcon type="primary" />
            </button>
          </header>
          {children}
        </div>
      </div>
    ), document.getElementById("root-modal")
  );
}

Modal.propTypes = {
    children: PropTypes.node.isRequired,
    onClose: PropTypes.func.isRequired
}

export default Modal;
