import React from "react";
import BurgerIngredientsCardStyles from "./burger-ingredients-card.module.css"; 
import { Counter, CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerIngredientsCard(props) {
  return (
    <div className={BurgerIngredientsCardStyles.card}>
      <Counter count={1} size="default" />
      <img className={`${BurgerIngredientsCardStyles.image} pl-4 pr-4`} src={props.image} alt={props.name} />
      <div className={BurgerIngredientsCardStyles.price}>
        <span className="text text_type_digits-default">{props.price}</span>
        <CurrencyIcon />
      </div>
      <h3 className="text text_type_main-default">{props.name}</h3>
    </div>
  )
}

export default BurgerIngredientsCard;
